import React, { useId } from 'react'

/* Packages */
import { useFormContext } from 'react-hook-form'

/**
 * Input
 * @param {string} id
 * @param {string} name (required)
 * @param {string} value
 * @param {object} validations
 * @param {...any} props
 * @returns
 */

export const Input = ({
  id,
  name = 'nameRequired!',
  value,
  validations,
  ...props
}) => {
  const ID = id ? `${name}:${id}` : `${name}${useId()}`
  const {
    register,
    formState: { errors }
  } = useFormContext()

  return (
    <React.Fragment>
      <input
        id={ID}
        name={name}
        defaultValue={value}
        {...register(name, { ...validations })}
        {...props}
      />
    </React.Fragment>
  )
}
