/* https://loading.io */

export const Waves = ({ colors, ...props }) => (
  <div {...props}>
    <svg
      xmlns="http://www.w3.org/2000/svg"
      xmlnsXlink="http://www.w3.org/1999/xlink"
      width="200px"
      height="200px"
      viewBox="0 0 100 100"
      preserveAspectRatio="xMidYMid"
    >
      <circle
        cx="50"
        cy="50"
        r="0"
        fill="none"
        stroke={colors[1]}
        strokeWidth="3"
      >
        <animate
          attributeName="r"
          repeatCount="indefinite"
          dur="1s"
          values="0;40"
          keyTimes="0;1"
          keySplines="0 0.2 0.8 1"
          calcMode="spline"
          begin="0s"
        ></animate>
        <animate
          attributeName="opacity"
          repeatCount="indefinite"
          dur="1s"
          values="1;0"
          keyTimes="0;1"
          keySplines="0.2 0 0.8 1"
          calcMode="spline"
          begin="0s"
        ></animate>
      </circle>
      <circle
        cx="50"
        cy="50"
        r="0"
        fill="none"
        stroke={colors[2]}
        strokeWidth="3"
      >
        <animate
          attributeName="r"
          repeatCount="indefinite"
          dur="1s"
          values="0;40"
          keyTimes="0;1"
          keySplines="0 0.2 0.8 1"
          calcMode="spline"
          begin="-0.5s"
        ></animate>
        <animate
          attributeName="opacity"
          repeatCount="indefinite"
          dur="1s"
          values="1;0"
          keyTimes="0;1"
          keySplines="0.2 0 0.8 1"
          calcMode="spline"
          begin="-0.5s"
        ></animate>
      </circle>
    </svg>
  </div>
)
/* Packages */
import styled from 'styled-components'

/* Loaders */
import { Waves } from './loader'

export const WavesStyled = styled(Waves)`
  background-color: rgba(255, 255, 255, .3);
  height: 100%;
  left: 0px;
  position: fixed;
  top: 0px;
  width: 100%;
  z-index: 9999999999999;

  & svg {
    background: none;
    display: block;
    left: 50%;
    margin: auto;
    position: absolute;
    shape-rendering: auto;
    top: 50%;
    transform: translate(-50%, -50%);
  }

  @supports ((-webkit-backdrop-filter: none) or (backdrop-filter: none)) {
    backdrop-filter: blur(5px);
    background-color: rgba(255, 255, 255, .3);
  }
`
/* Default */
import React, { useState, useEffect } from 'react'
import { useRouter } from 'next/router'

/* Context */
const LoadingContext = React.createContext()

/* Styles */
import { WavesStyled } from './styles'

const LoadingProvider = ({ children }) => {
  const router = useRouter()
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    const handleLoadingEnabled = () => setLoading(true)
    const handleLoadingDisabled = () => setLoading(false)

    router.events.on('routeChangeStart', handleLoadingEnabled)
    router.events.on('routeChangeComplete', handleLoadingDisabled)

    return () => {
      router.events.off('routeChangeStart', handleLoadingEnabled)
      router.events.off('routeChangeComplete', handleLoadingDisabled)
    }
  }, [])

  return (
    <LoadingContext.Provider value={{ loading, setLoading }}>
      {loading && <WavesStyled colors={{ 1: 'yellow', 2: 'black' }} />}
      {children}
    </LoadingContext.Provider>
  )
}

export default LoadingProvider
export { LoadingContext }
/* Default */
import { useContext, useEffect } from 'react'

/* Contexts */
import { LoadingContext } from '../contexts/loading'

/**
 * useLoading
 * @param {boolean} - Whether to show the loading or not - default: false
 * @returns {object} - Object with the loading state and a function to set it - (e.g. { loading, setLoading })
 */

export const useLoading = (state) => {
  const { loading, setLoading } = useContext(LoadingContext)

  useEffect(() => {
    if (state) setLoading(true)
    else setLoading(false)
  }, [state])

  return {
    loading,
    setLoading
  }
}
