const breakpoints = {
  _xs: '374px',
  _s: '599px',
  _m: '767px',
  _l: '1023px',
  _xl: '1365px',

  xs: '375px',
  s: '600px',
  m: '768px',
  l: '1024px',
  xl: '1366px'
}

export default breakpoints
/* Packages */
import { createGlobalStyle } from 'styled-components'

/* Breakpoints */
import breakpoints from './breakpoints'

export const NormalizeCss = createGlobalStyle`
  :root {
    --xs: ${breakpoints.xs};
    --s: ${breakpoints.s};
    --m: ${breakpoints.m};
    --l: ${breakpoints.l};
    --xl: ${breakpoints.xl};
  }

  :root {
    /* prettier-ignore */
    --secure-font: -apple-system, BlinkMacSystemFont, 'Segoe UI','Source Sans Pro', Oxygen, sans-serif;
    --main-font: 'Source Sans Pro';
    --body-font-family: var(--main-font), var(--secure-font);
    --body-font-size: 16px;
  }

  * {
    box-sizing: border-box;
    outline: none;
    outline: 0;
  }
  *:focus {
    outline: none;
    outline: 0;
  }

  html,
  body {
    font-family: var(--body-font-family);
    font-size: var(--body-font-size);
    height: 100%;
    margin: 0 auto;
    padding: 0;
    position: relative;
    width: 100%;
  }

  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    font-family: var(--body-font-family);
    margin: 0px;
    padding: 1rem 0px;
    position: relative;
    white-space: pre-line;
  }

  a {
    cursor: pointer;
    text-decoration: none;
    transition: all 0.2s ease, outline 0.000001s;
  }
  a:active,
  a:hover {
    text-decoration: none;
  }
  a:focus {
    color: inherit;
  }

  img,
  svg {
    border: 0;
    border: none;
    display: inline-block;
    outline: 0;
    outline: none;
    vertical-align: middle;
    user-select: none;
  }

  p {
    margin: 0px;
    padding: 0.3125rem 0px;
    white-space: pre-line;
  }

  hr {
    border: none;
    border-top: solid 1px;
  }
`
/* Packages */
import styled, { css } from 'styled-components'
import * as ss from 'styled-system'

export const Box = styled.div`
  display: block;
  margin: 0 auto;
  padding: 0rem 0.625rem;
  width: 100%;

  ${(props) =>
    !props.fullWidth &&
    css`
      max-width: 85.375rem;
    `}

  ${ss.compose(ss.layout, ss.position, ss.space)}
`
/* Styles */
import { Box as BoxStyled } from './styles'

/**
 * Box - max-width: 1366px
 * @docs layout - https://styled-system.com/table#layout
 * @docs position - https://styled-system.com/table#position
 * @docs space - https://styled-system.com/table#space
 * @param {boolean} fullWidth - max-width property will not apply
 * @param {...any} props
 * @returns component
 */

export const Box = ({ children, ...props }) => {
  return <BoxStyled {...props}>{children}</BoxStyled>
}
/* Packages */
import styled, { css } from 'styled-components'
import * as ss from 'styled-system'

export const Button = styled.a`
  color: inherit;
  cursor: pointer;
  transition: all 0.2s ease;

  ${(props) =>
    props.disabled &&
    css`
      cursor: not-allowed;
      filter: grayscale(100%);
      opacity: 0.5;
      pointer-events: none;
    `}

  ${ss.compose(ss.color, ss.layout, ss.position, ss.space, ss.typography)}
`
/* Default */
import React from 'react'
import Link from 'next/link'

/* Styles */
import { Button as StyledButton } from './styles'

/**
 * Button
 * @param {children}
 * @param {string} label - Label to display in the button - disabled if has children
 * @param {string} href - Href of the link
 * @param {string} target - Target of the link - (e.g. _blank)
 * @param {boolean} scroll - Go to top on click - default: true
 * @param {props}
 * @returns
 *
 * @styleSystem [color, layout, position, space, typography] - https://styled-system.com/table
 */

export const Button = React.forwardRef(({ scroll = true, ...props }, ref) => {
  return (
    <React.Fragment>
      {((props.target || !props.href) && (
        <A ref={ref} rel={props.target ? 'noreferrer noopener' : null} {...props} />
      )) || (
        <Link href={props.href} passHref scroll={scroll}>
          <A ref={ref} {...props} />
        </Link>
      )}
    </React.Fragment>
  )
})

Button.displayName = 'Button'

const A = React.forwardRef(({ children, label, ...props }, ref) => {
  return (
    <StyledButton
      ref={ref}
      type={props.as === 'button' ? 'submit' : null}
      {...props}
    >
      {(children && children) || <span>{label}</span>}
    </StyledButton>
  )
})

A.displayName = 'A'
/* Packages */
import styled from 'styled-components'
import * as ss from 'styled-system'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

export const Icon = styled(FontAwesomeIcon)`
  height: 1em;
  ${ss.compose(ss.color, ss.layout, ss.position, ss.space, ss.typography)}
`

export default FontAwesomeGlobalStyle
/* Default */
import React from 'react'

/* Packages */
import { library } from '@fortawesome/fontawesome-svg-core'

/* Styles */
import { Icon } from './styles'

/* Libraries */
import { fab } from '@fortawesome/free-brands-svg-icons'
import { far } from '@fortawesome/free-regular-svg-icons'
import { fas } from '@fortawesome/free-solid-svg-icons'

/* Settings */
library.add(fab, far, fas)

/**
 * FontAwesomeIcon
 * @param {string} icon (required) - The icon to use. (e.g. 'chevron-right')
 * @param {string} lib - The library to use. - (e.g. 'fas', 'fab', 'far') - default: 'fas'
 * @param {object} props - The props to pass to the component.
 * @returns
 *
 * @docs https://fontawesome.com/search
 * @styleSystem [color, layout, position, space, typography] - https://styled-system.com/table
 */

export const FontAwesomeIcon = ({ icon, lib = 'fas', ...props }) => {
  return <Icon icon={[lib, icon]} {...props} />
}
